import { useState, useRef } from "react";
import { Card, Image, Form, Input, Button } from "antd";
import { EditOutlined, PropertySafetyFilled } from "@ant-design/icons";
import PropTypes from "prop-types";

export default function ListMain(props) {
  const [editPerson, setEditPerson] = useState(false);
  const refInput = useRef(null);

  let { first, last } = props.person.name;
  let { large } = props.person.picture;
  let { state, city } = props.person.location;

  let fullName = first + " " + last;
  let cityState = city + ", " + state;

  let listInfoDiv = "";
  let nameSpan = "";

  /*
   <Form
          name="personLocationContactForm"
          layout="vertical"
          size="small"
          onFinish={(data) => {
            
            console.log(refInput.state.value)
            let emailData = { id: props.id,  };
            props.editPersonFn();
            
          }}
          onFinishFailed={(err) => {
            props.editFailedFn(err);
            setEditPerson(false);
          }}
          autoComplete="off"
        >
          <Form.Item
            label="Email"
            name="email"
            rules={[
              {
                required: true,
                type: "email",
                message: "Please input a valid email!"
              }
            ]}
            ref={refInput}
          >
            <Input />
          </Form.Item>

          <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
           
          </Form.Item>
        </Form>
  */

  if (editPerson) {
    nameSpan = <span className="listName">{fullName}</span>;
    listInfoDiv = (
      <div className="listInfo">
        <form
          name="personLocationContactForm"
          onSubmit={(data) => {
          console.log(data)
          props.editPersonFn(data);
          setEditPerson(false);
          }}>
            <label> Email:
              <Input type="email" placeholder={person.email} />
            </label>
          <Button danger size="small" onClick={() => setEditPerson(false)}>
            Cancel
          </Button>
          <Button type="primary" htmlType="submit">
              Submit
          </Button>
        </form>
      </div>
    );
  } else {
    nameSpan = <span className="listName">{fullName}</span>;
    listInfoDiv = (
      <div className="listInfo">
        <p>{props.person.email}</p>
        <p>{props.person.phone}</p>
        <p>{cityState}</p>
      </div>
    );
  }

  return (
    <Card
      className="listCard"
      hoverable
      bordered={false}
      loading={props.loading}
    >
      <div className="listHeader">
        <span className="listIcon" onClick={() => setEditPerson(!editPerson)}>
          <EditOutlined />
        </span>
        {nameSpan}
      </div>
      <div className="listImg">
        <Image src={large} alt={fullName + " image"} preview={false} />
      </div>
      {listInfoDiv}
    </Card>
  );
}

ListMain.propTypes = {
  editPersonFn: PropTypes.func,
  editFailedFn: PropTypes.func,
  person: PropTypes.object,
  loading: PropTypes.bool
};
